mapper.update:
UPDATE tb_product
product_name=#{productName}, img_addr=#{imgAddr}, normal_price=#{normalPrice}, promote_price=#{promotePrice}, priority=#{priority}, create_time=#{createTime}, last_edit_time=#{lastEditTime}, enable_status=#{enableStatus},
product_category_id = #{productCategory.productCategoryId}
WHERE product_id=#{productId} and shop_id=#{shop.shopId}

mapper.delete:
DELETE FROM tb_product
WHERE
    product_id=#{productId} and shop_id=#{shopId}

mapper.select:
SELECT p.product_id, p.product_name, p.img_addr, p.product_desc, p.normal_price, p.promote_price, p.priority, p.create_time, p.last_edit_time, p.enable_status, p.product_category_id, p.shop_id, pm.product_img_id, pm.img_address, pm.img_desc, pm.create_time, pm.priority as detail_img_priority FROM tb_product as p LEFT JOIN tb_product_img as pm ON p.product_id=pm.product_id
and p.shop_id=#{productCondition.shop.shopId}
and p.product_category_id=#{productCondition.productCategory.productCategoryId}
and p.product_name like '%${productCondition.productName}%'
and p.enable_status = #{productCondition.enableStatus}
ORDER BY p.priority DESC LIMIT #{rowIndex}, #{pageSize};

SELECT count(1)
FROM
    tb_product


        and shop_id = #{productCondition.shop.shopId}


        and product_category_id = #{productCondition.productCategory.productCategoryId}


        and product_name like '%${productCondition.productName}%'


        and enable_status = #{productCondition.enableStatus}
